    margin-bottom: 1.5rem;
    border-left: 5px solid var(--color-accent);
  }
}

.section-container {
  width: 100%;
  max-width: var(--max-width, 1200px);
  margin: 0 auto;
  padding: 5rem 2rem;
}

.heading {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  line-height: 1.1;
  font-weight: 500;
  color: #333;
  @media (min-width: 800px) {
    font-size: 2.75rem;
  }
}

.button {
  color: #f6f6f9;
  font-size: 1rem;
  font-weight: 500;
  border-radius: 2rem;
  padding: 10px 24px;

  background: var(--color-accent);
  transition: var(--transition);
  white-space: nowrap;

  &:hover {
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent);
    border: 2px solid var(--color-accent);
  }
}

@keyframes pulse {
  from {
    transform: scale(0.75);
  }

  to {
    transform: scale(1);
  }
}

/* Screen-readers only */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
