let active_index = Math.floor(Math.random() * images.length);
$: active_item = images[active_index];

function show_previous_item() {
  let next_item = active_index - 1;
  if (next_item < 0 || next_item >= images.length) {
    next_item = images.length-1;
  }
  active_index = next_item;
  console.log("Next image ",active_index);
}

function show_next_item() {
  let next_item = active_index + 1;
  if (next_item < 0 || next_item >= images.length) {
    next_item = 0;
  }
  active_index = next_item;
}

function show_item(i) {
  if (i < 0) {
    active_index = 0;
  } else if (i >= images.length) {
    active_index = images.length-1;
  } else {
    active_index = i;
  }
}

